#-------------------------------------------------------------------------------------------------#
#Return List of edges of a graph

def listOfEdges(G, directed):
    edges = []
    check_lst = []
    for key in G:
        vals = G.get(key)
        for x in range(len(vals)):
            if directed == True:
                edges.append((key, vals[x][0], vals[x][1]))
            else:
                if (vals[x][0], key, vals[x][1]) in edges:
                    pass
                else:
                    edges.append((key, vals[x][0], vals[x][1]))
    return edges

