#Print the degree of each node in a Graph

def printDegree(G):
    nodes, degrees = [], []
    for key in G:
        nodes.append(key)
        degrees.append(len(G.get(key)))
    for x in range(len(nodes)):
        print(f"{nodes[x]} => {degrees[x]}")

