#-------------------------------------------------------------------------------------------------#
#Weighted undirected Graph is given and a node, return its nearest neighbour - neighbour with lowest weight
# G = { 0: [(1, 21), (2, 15)],
# 1: [(0, 21), (2, 10), (3, 70)],
# 2: [(0, 15), (1, 10), (4, 50)],
# 3: [(1, 70), (4, 24), (5, 39)],
# 4: [(3, 24), (2, 50), (5, 99)],
# 5: [(3, 39), (4, 99)] }
def getNearestNeighbor(G, node):
    lst = [G[node][0][0]]
    check = G[node][0][1]
    for i in G[node]:
        if i[1] < check:
            check = i[1]
            lst[0] = i[0]
    return lst[0]

