# 4: [(5, 99)],
# 5: [] }

def display_adj_matrix(G):
    mat = []
    for node in G:
      mat_zeroes = [0] * len(G)
      for values in G[node]:
        mat_zeroes[values[0] - 1] = values[1]
      mat.append(mat_zeroes)
    print(mat)

