"""**Stack and Queue Helper Functions**"""

#Stack helper functions
def push(stack, element):
  stack.append(element)

def pop(stack):
  return stack.pop(-1)

def top(stack):
  return stack[-1]

def is_empty(stack):
  if len(stack) == 0:
    return True
  else:
    return False

#Queue helper functions
def enQueue(lst, data):
  lst.append(data)
    
def deQueue(lst):
  return lst.pop(0)

def front(lst):
  return lst[0]

