# Recursive Approach for DFS (required to do it iteratively in our Lab with the use of stacks - hence iterative approach too)
# def dfs(graph, node, visited):
#   if node not in visited:
#     visited.append(node)
#     # print(visited)
#     for neighbor in graph[node]:
#       dfs(graph, neighbor[0], visited)
#     return visited

# visited = []
# dfs(G, 0, [])

def dfs(graph, node):
  visited = []
  stack = []
  push(stack, node)
  while not is_empty(stack):
    n = pop(stack)
    if n not in visited:
      visited.append(n)
      for neighbor in graph[n]:
        if neighbor[0] not in visited:
          push(stack, neighbor[0])
  return visited

print("\n")

for vertex in nodes:
  print(f"List of visited vertices starting from {vertex} are {dfs(G, vertex)}")

