#Approach using Depth First Search
def check_cycles(G, cycle):
  first = cycle[0]
  path = dfs(G, first)
  path = path[0:len(cycle)]
  if path == cycle:
    neighbors = getOutNeighbors(G, path[-1])
    if first in neighbors:
      return "Yes"
  return "No"

