#Solution to Exercise 4 (b)
def get_node_level(G, node):
  visited, queue = [], []
  first = next(iter(G))
  if node == first:
    return 0
  levels = {first : 0}
  visited.append(first)
  enQueue(queue, first)
  while not is_empty(queue):
    vertex = deQueue(queue)
    neighbors = getOutNeighbors(G, vertex)
    for neighbor in neighbors:
      levels[neighbor] = levels[vertex] + 1
      if neighbor == node:
        return levels[neighbor]
      if neighbor not in visited:
        visited.append(neighbor)
        enQueue(queue, neighbor)

print("The level of node S is", get_node_level(G, "S"))
for i in range(1, 8):
  print(f"The level of node {i} is {get_node_level(G, i)}")
